INSERT INTO definition (word, definition, theme_id) VALUES
('диэнцефальный синдром при гипотиреоидной коме', '- это микседематозная кома с гипотермией. Симптомы: брадикардия. Диагностика: ТТГ. Лечение: левотироксин IV. Прогноз: летальность 40%.', 12);
INSERT INTO definition (word, definition, theme_id) VALUES
('дистрофия печени при ПБЦ', '- это первичный билиарный цирроз с зудом. Симптомы: ксантомы. Диагностика: AMA. Лечение: урсодезоксихолевая кислота. Прогноз: трансплантация.', 12);
INSERT INTO definition (word, definition, theme_id) VALUES
